DROP DATABASE IF EXISTS postimerkkikanta;

CREATE DATABASE postimerkkikanta 
DEFAULT CHARACTER SET = utf8
COLLATE = utf8_swedish_ci;

USE postimerkkikanta;

CREATE TABLE Postimerkki (
  id                INTEGER NOT NULL PRIMARY KEY AUTO_INCREMENT,
  asiasanat         TEXT,
  ilmestymispaiva   TEXT,
  kaytonPaattyminen TEXT,
  nimellisarvo      FLOAT,
  merkinNimi        TEXT,
  merkinVari        TEXT,
  painopaikka       TEXT,
  painosmaara       INTEGER NOT NULL,
  taiteilija        TEXT,
  valuutta          TEXT,
  kuvanUrl          TEXT
)
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8
COLLATE = utf8_swedish_ci;

